import React from 'react'
import CoinTable from './components/data-table'

import data from './data/coins.json'

import './app.css'

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      data: data,
      direction: {
        price_usd: 'asc',
      }
    }

    this.sortBy = this.sortBy.bind(this)
  }


  render() {
    return (
      <div
        className="page-container"
      >
        <CoinTable
          data={this.state.data}
          sortBy={this.sortBy}
        />
      </div>
    )
  }
}

export default App
